def on_on_update():
    if Player_Control == True:
        controller.move_sprite(mySprite, 100, 0)
        Builder2.set_flag(SpriteFlag.INVISIBLE, True)
    elif Player_Control == False:
        Builder2.set_flag(SpriteFlag.INVISIBLE, False)
        controller.move_sprite(Builder2, 60, 60)
        controller.move_sprite(mySprite, 0, 0)
game.on_update(on_on_update)

def on_on_update2():
    if Player_Control == True:
        Builder2.set_position(mySprite.x, mySprite.y)
game.on_update(on_on_update2)
